import React from "react";
import { Person, Bag, Key, Arrow90degLeft } from "react-bootstrap-icons";
import ShopBanner from "../components/ShopBanner";
import AllPurchases from "../components/Purchases/AllPurchases";
import AccountSidebar from "../components/Account/AccountSidebar";

class Purchases extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "profile",
      profilePage: true,
      allPurchasesPage: false,
      passwordPage: false,
      returnPage: false,
    };
  }

  handleProfileClick = (e) => {
    this.toggleProfile();
  };

  toggleProfile() {
    this.setState({ title: "profile" });
    this.setState({ profilePage: true });
    this.setState({ allPurchasesPage: false });
    this.setState({ returnPage: false });
    this.setState({ passwordPage: false });
  }

  handlePasswordClick = (e) => {
    this.togglePassword();
  };

  togglePassword() {
    this.setState({ title: "password" });
    this.setState({ profilePage: false });
    this.setState({ allPurchasesPage: false });
    this.setState({ passwordPage: true });
    this.setState({ returnPage: false });
  }

  handleAllPurchasesClick = (e) => {
    this.togglePurchases();
  };

  togglePurchases() {
    this.setState({ title: "My Purchases" });
    this.setState({ profilePage: false });
    this.setState({ allPurchasesPage: true });
    this.setState({ passwordPage: false });
    this.setState({ returnPage: false });
  }

  handleReturnsClick = (e) => {
    this.toggleReturns();
  };

  toggleReturns() {
    this.setState({ title: "Returns" });
    this.setState({ profilePage: false });
    this.setState({ allPurchasesPage: false });
    this.setState({ passwordPage: false });
    this.setState({ returnPage: true });
  }

  render() {
    return (
      <main>
        <ShopBanner title="Purchases" />
        <section>
          <div className="container">
            <div className="row">
              <div className="col-12 col-md-4 col-lg-3">
                <AccountSidebar />
              </div>
              <div className="col-12 col-md-8 col-lg-9">
                <div className="shop_grid_product_area ml-5 mr-5">
                  <AllPurchases />
                </div>
              </div>
            </div>
          </div>
        </section>
      </main>
    );
  }
}

export default Purchases;
