import React, { Component } from "react";
import { ChevronRight } from "react-bootstrap-icons";

class PurchasesRow extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    let date = new Date(this.props.order.date);
    let orderDate = `${date.getDate()}-${
      date.getMonth() + 1
    }-${date.getFullYear()}`;

    return (
      <div className="product border-bottom p-3">
        <div className="row">
          <div className="col-md-8">
            <div className="info">
              <div className="row">
                <div className="col-md-7 ml-4 mt-3">
                  <h6>Order #{this.props.order._id}</h6>
                  <p>{this.props.order.products.length} Items</p>
                </div>

                <div className="col-md-3 quantity mt-3">
                  <p>{orderDate}</p>
                  <p>â‚¬{this.props.order.amount}</p>
                </div>
                <div className="col-md-4 price mt-3">
                  <ChevronRight />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default PurchasesRow;
