import React from "react";

class OrderDetailsRow extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    return (
      <div className="product border-bottom mt-0 mb-2">
        <div className="row">
          <div className="col-md-3"></div>
          <div className="col-md-8">
            <div className="info">
              <div className="row">
                <div className="col-md-5">
                  <div>
                    <h6>{this.state.productName}</h6>
                  </div>
                </div>
                <div className="col-md-4 quantity">
                  <label>Quantity:</label>
                  <br />
                  <button
                    className="btn rounded-circle mr-3"
                    onClick={this.handleDecreaseQuantity}
                  >
                    -
                  </button>
                  {this.state.quantity}

                  <span className="">
                    <button
                      className="btn rounded-circle ml-3"
                      onClick={this.handleIncreaseQuantity}
                    >
                      +
                    </button>
                  </span>
                </div>
                <div className="col-md-3 price">
                  <span>
                    â‚¬
                    {(Math.round(this.state.totalPrice * 100) / 100).toFixed(2)}
                  </span>
                  <Trash
                    onClick={this.handleRemoveItem}
                    className="ml-3 cursor-pointer"
                    color="#787878"
                    size={20}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default OrderDetailsRow;
