import React from "react";
import axios from "axios";
import { SERVER_HOST } from "../../config/global_constants";
import AllPurchasesRow from "./AllPurchasesRow";

class AllPurchases extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      orders: [],
    };
  }
  componentDidMount() {
    axios.defaults.withCredentials = true;

    axios
      .get(`${SERVER_HOST}/orders`, {
        headers: { authorization: localStorage.token },
      })
      .then((res) => {
        if (res.data) {
          if (res.data.errorMessage) {
            console.log(res.data.errorMessage);
          } else {
            console.log("Orders record read");
            this.setState({ orders: res.data });
          }
        } else {
          console.log("Orders record not found");
        }
      });
  }

  render() {
    return (
      <div>
        {this.state.orders !== undefined
          ? this.state.orders.map((order) => <AllPurchasesRow order={order} />)
          : null}
      </div>
    );
  }
}

export default AllPurchases;
