import React from "react";
import { SERVER_HOST } from "../../config/global_constants";
import axios from "axios";

class OrderDetailsRow extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      productName: "",
      price: 0,
      photos: [],
    };
  }
  componentDidMount() {
    axios
      .get(`${SERVER_HOST}/products/${this.props.product.productId}`)
      .then((res) => {
        this.setState({
          productName: res.data.productName,
          price: res.data.price,
          photos: res.data.photos,
        });

        res.data.photos.map((photo) => {
          return axios
            .get(`${SERVER_HOST}/products/photo/${photo.filename}`)
            .then((res) => {
              if (res.data) {
                if (res.data.errorMessage) {
                  console.log(res.data.errorMessage);
                } else {
                  document.getElementById(
                    photo._id
                  ).src = `data:;base64,${res.data.image}`;
                }
              } else {
                console.log("Record not found");
              }
            });
        });
      });
  }

  render() {
    let totalPrice = this.state.price * this.props.product.quantity;
    return (
      <div className=" mt-0 mb-2 d-flex align-items-center justify-contents-center p-2 ">
        <div className="row">
          <div className="">
            {this.state.photos.map((photo, index) =>
              index === 0 ? (
                <img
                  className="rounded "
                  key={photo._id}
                  id={photo._id}
                  alt="Product"
                  width="200"
                />
              ) : null
            )}
          </div>
          <div className=" d-flex align-items-center justify-content-center w-80">
            <div className="info">
              <div className="row">
                <div className="col-md-5 ml-3">
                  <div>
                    <h6>{this.state.productName}</h6>
                  </div>
                </div>
                <div className=" ">
                  <label>Quantity:</label>

                  <span className=""> {this.props.product.quantity}</span>
                </div>
                <div className="">
                  <span>
                    â‚¬{(Math.round(totalPrice * 100) / 100).toFixed(2)}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default OrderDetailsRow;
