import React from "react";
import axios from "axios";
import { SERVER_HOST } from "../../config/global_constants";
import ShopBanner from "../ShopBanner";
import { Carousel } from "react-bootstrap";

class OrderDetails extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      user: "",
    };
  }

  componentDidMount() {
    axios.defaults.withCredentials = true;
    let products = [];
    axios
      .get(`${SERVER_HOST}/orders/${this.props.match.params.id}`, {
        headers: { authorization: localStorage.token },
      })
      .then((res) => {
        res.data.products.forEach((productId) => {
          axios.get(`${SERVER_HOST}/products/${productId}`).then((res) => {
            products.push(res.data);
            this.setState({ products: products });
          });
        });

        if (res.data.userId !== "undefined") {
          axios
            .get(`${SERVER_HOST}/users/${res.data.userId}`, {
              headers: { authorization: localStorage.token },
            })
            .then((res) => {
              this.setState({ user: res.data.email });
            });
        } else {
          this.setState({ user: "guest" });
        }

        res.data.products.photos.map((photo) => {
          return axios
            .get(`${SERVER_HOST}/products/photo/${photo.filename}`)
            .then((res) => {
              if (res.data) {
                if (res.data.errorMessage) {
                  console.log(res.data.errorMessage);
                } else {
                  document.getElementById(
                    photo._id
                  ).src = `data:;base64,${res.data.image}`;
                }
              } else {
                console.log("Record not found");
              }
            });
        });
      });
  }

  render() {
    return (
      <div>
        <ShopBanner title="Order Details" />
        {console.log(this.state.products)}
        {console.log(this.state.user)}
        <div className="product border-bottom mt-0 mb-2">
          <div className="row">
            <div className="col-md-3">
              <Carousel nextLabel="" prevLabel="" indicators={false}>
                {this.state.photos.map((photo) => (
                  <Carousel.Item key={photo._id}>
                    <img className="rounded" key={photo._id} id={photo._id} />
                  </Carousel.Item>
                ))}
              </Carousel>
            </div>
            <div className="col-md-8">
              <div className="info">
                <div className="row">
                  <div className="col-md-5">
                    <div>
                      <h6>{this.state.productName}</h6>
                    </div>
                  </div>
                  <div className="col-md-4 quantity">
                    <label>Quantity:</label>
                    <br />
                    <button
                      className="btn rounded-circle mr-3"
                      onClick={this.handleDecreaseQuantity}
                    >
                      -
                    </button>
                    {this.state.quantity}

                    <span className="">
                      <button
                        className="btn rounded-circle ml-3"
                        onClick={this.handleIncreaseQuantity}
                      >
                        +
                      </button>
                    </span>
                  </div>
                  <div className="col-md-3 price">
                    <span>
                      â‚¬
                      {(Math.round(this.state.totalPrice * 100) / 100).toFixed(
                        2
                      )}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default OrderDetails;
